import React, {useState} from "react";
import ButtonGroup from '@mui/material/ButtonGroup';
import Tab from "../IDE/Tab";

import TextPopup from "./TextPopup";


function Tabs(props) {

    const [activeTab, setActiveTab] = useState(0);

    const [tabs, setTabs] = useState([{
        color: "primary",
        name: "Main",
        onClick: null
    }]);

    // Tab target corresponds to index in tabs array
    function switchTabs(tabTarget){
        setTabs((prev) => {
            prev[activeTab].color = "inherit";
            prev[tabTarget].color = "primary";
            return prev;
        })
        setActiveTab(tabTarget);
        
    }

    function addTab(tabName) {
        // Note: Color switching is delayed if tabs switched after add
        setTabs((prev) => {
            prev[activeTab].color = "inherit";
            return prev;
        })

        setTabs((prev) => {
            return ([...prev, {
                color: "primary",
                outlined: "primary",
                name: tabName,
                onClick: null
            }])
        })

        switchTabs(tabs.length);
    }

    

    return (
        <div>
            <div className="flex -mb-4">
                <ButtonGroup variant="contained" aria-label="outlined primary button group">
                    {
                        tabs.map((tab, index) => {
                            return (<Tab key={index} id={index} color={tab.color} name={tab.name} onClick={() => {addTab(index)}} />)
                        })
                    }
                    {/*<Tab color="inherit" name="File 2" />*/}
                    
                    
                </ButtonGroup>
                <div className="mx-4">
                    <TextPopup formTitle="Create a new REPL" formBody="Enter a name for your new REPL environment: " addREPL={addTab} />
                </div>
                
            </div>
            
        </div>
    )

}


export default Tabs;
