import React, {useState} from "react";
import Header from "./Header";
import IDE from "./IDE";


function App() {

    const [editors, setEditors] = useState([{
        name: "Main",
        id: 0,
        code: ""
    }]);

    const [numEditors, setNumEditors] = useState(1);


    // Make sure editor names are unique!
    function addEditor(newEditorName) {
        setNumEditors((prev) => {
            return (prev + 1);
        })
        setEditors((prev) => {
            return ([...prev, {
                name: newEditorName,
                id: (prev.length + 1),
                code: ""
            }])
        })
    }

    return (
        <div>
            <Header />
            { editors.map((editor) => {
                    return (
                        <IDE key={editor.id} />
                    )
                })
            }

            <IDE />

        </div>
    );
}


export default App;
