import React, {useRef, useEffect} from "react";

function Console(props) {

    function strToASCII(s){
        let charCodeArr = [];
        
        for(let i = 0; i < s.length; i++){
            let code = s.charCodeAt(i);
            charCodeArr.push(code);
        }
        
        return charCodeArr;
    }

    const scrollRef = useRef(null);

    useEffect(() => {
        scrollRef.current.scrollIntoView({ behavior: 'smooth' });
    });

    return (
        <div className="border border-black max-h-96 overflow-scroll">
            <p className="text-decoration-line: underline text-center">Console</p>
            <br />
                
            
            {(props.content.split('\n')).map((element, index) => {
                console.log(element);
                console.log(strToASCII(element));
                return (
                <div ref={scrollRef} key={index}>
                    <p>{element}</p>
                </div>)
            })}
        
        </div>
    )
}

export default Console;
