import React, { useState } from "react";
import AceEditor from 'react-ace';
import "ace-builds/src-noconflict/mode-python";
import "ace-builds/src-noconflict/theme-github";

function CodeEditor(props) {

    
    const initCode = 
`from spike import PrimeHub, LightMatrix, Button, StatusLight, ForceSensor, MotionSensor, Speaker, ColorSensor, App, DistanceSensor, Motor, MotorPair \nfrom spike.control import wait_for_seconds, wait_until, Timer \n\n`;

    const [curCode, setCurCode] = useState(initCode);

    function onType() {
        console.log(curCode)
    }

    return (

        
        
        
        <div className="w-full border border-black">
            {
                <AceEditor
                    onChange={onType}
                    mode="python"
                    theme="github"
                    width="500px"
                    height="500px"
                    value={curCode} 
                />
            }
        </div>
    )
        
    
}

export default CodeEditor;
