import 'prismjs/components/prism-clike';
import 'prismjs/components/prism-javascript';
import 'prismjs/themes/prism.css'; //Example style, you can use another

function CodeEditor(props) {

    const initCode = 
`#Remove comments if you need to import from 'spike' modules \n#from spike import PrimeHub, LightMatrix, Button, StatusLight, ForceSensor, MotionSensor, Speaker, ColorSensor, App, DistanceSensor, Motor, MotorPair \n#from spike.control import wait_for_seconds, wait_until, Timer \n\n`;

    const [code, setCode] = useState(initCode);
    const editorRef = useRef(null);

    function textChange(event) {

        console.log(code);
    }

    return (
        <div className="w-full">
            <Editor 
                value={code}
                onValueChange={code => setCode(code)}
                highlight={code => highlight(code, languages.js)}
                padding={10}
                style={{
                    fontFamily: '"Fira code", "Fira Mono", monospace',
                    fontSize: 12,
                }}
            />
            {/*
            <AceEditor
                id={props.id}
                ref={editorRef}
                onChange={() => {console.log(document.getElementById(props.id))}}
                mode="python" 
                theme="monokai" 
                value={initCode}
                
            />
            */}
        </div>
    )
}

export default CodeEditor;
