import React, {useState} from "react";
import SerialButton from "./SerialButton";
import Tooltip from "@mui/material/Tooltip";
import PlayCircleIcon from '@mui/icons-material/PlayCircle';
import Fab from '@mui/material/Fab';



function Serial(props) {

    

    return (
        <div className="flex justify-center">
            
            <SerialButton 
                on={serialOn} 
                offColor="error"
                onColor="success" 
                connectText={connectText} 
                
                onClick={() => {
                    connectToSPIKE();
                }} 
                restartClick={() => {
                    console.log("Close?");
                }} 
                getCurrentCode={props.getCurrentCode}
                runCurrentCode={writeToPort}
            />

            {/* RUN BUTTON Displayed when SPIKE IS connected */}
            <p className={!serialOn ? "hidden" : "mt-2 mx-2 ml-8"}>Run Code:</p>
            <div className={!serialOn ? "hidden" : null}>
                <Tooltip title="Run Current File" placement="right">
                    <Fab 
                        onClick={runCurrentCode} 
                        color="info" 
                        aria-label="add" 
                        size="small">

                        <PlayCircleIcon />
                    </Fab>
                </Tooltip>
            </div>

        </div>
    )
}

export default Serial;
