import React, {useState} from "react";
import Fab from '@mui/material/Fab';
import CableIcon from '@mui/icons-material/Cable';
import Tooltip from "@mui/material/Tooltip";

/*
    InitWebSerial
    - Initializes a Web Serial Port and returns the initialized port
    - Returns the port if successful, null otherwise
*/

let port = null;

async function initWebSerial() {
    port = await navigator.serial.getPorts();

    port = await navigator.serial.requestPort({
        // filters:[filter]
    });

    // wait for the port to open.
    try {
        await port.open({ baudRate: 115200 });
    }
    catch (er) {
        console.log("%cTuftsCEEO ", "color: #3ba336;", er);
        await port.close();
    }

    if (port.readable) {
        return true;
    }
    else {
        return false;
    }
}

async function readPort() {
    while (port.readable) {
        const reader = port.readable.getReader();
        try {
            while (true) {
            const { value, done } = await reader.read();
            if (done) {
                // |reader| has been canceled.
                break;
            }
            console.log(value);
            }
        } catch (error) {
            console.error(error);
        } finally {
            reader.releaseLock();
        }
    }
}


async function writeToPort(lines) {
    // Uncessary Error (linter) from encoder definition
    
    const encoder = new TextEncoder();

    // eslint-disable-next-line no-undef

    //const outputDone = encoder.readable.pipeTo(port.writable);
    const outputStream = encoder.writable;

    const writer = outputStream.getWriter();
    lines.forEach((line) => {
        console.log('[SEND]', line);
        writer.write(line + '\n');
    });
    writer.releaseLock();
}

async function startWebSerial() {
    if (await initWebSerial()) {
        await writeToPort(['\x03'])
        
        readPort()
    }

}

function Serial() {
    const [connectText, setConnectText] = useState("Connect SPIKE Here: ");

    return (
        <div className="flex justify-center">
            <p className="mt-2 mx-2">{connectText}</p>
            <Tooltip title="Connect Device" placement="right">
                <Fab onClick={() => {startWebSerial()}} color="warning" aria-label="add" size="small">
                    <CableIcon />
                </Fab>
            </Tooltip>


        </div>
    )
}

export default Serial;
