async function initWebSerial() {
    
    /*
    const usbVendorId = "0694";
    navigator.serial.requestPort({ filters: [{ usbVendorId }]}).then((port) => {
        console.log("Success!");
    }).catch((e) => {
        console.log("Error" + e);
    });
    */
    let port = await navigator.serial.getPorts();
    
    let success = false;

    port = await navigator.serial.requestPort({
        // filters:[filter]
    });
    // wait for the port to open.
    try {
        await port.open({ baudRate: 115200 });
    }
    catch (er) {
        console.log("%cTuftsCEEO ", "color: #3ba336;", er);
        await port.close();
    }

    if (port.readable) {
        success = true;
    }
    else {
        success = false;
    }

    return success;

}

export default initWebSerial;
