// Taken from https://stackoverflow.com/questions/37440408/how-to-detect-esc-key-press-in-react-and-how-to-handle-it


// Run Code using shortcut CTRL + 
import React, { useEffect } from 'react';

const useEscape = (onEnter) => {
    useEffect(() => {
        const handleEsc = (e) => {
            if (e.keyCode===13 && e.ctrlKey) 
                onEnter();
        };
        window.addEventListener('keydown', handleEsc);

        return () => {
            window.removeEventListener('keydown', handleEsc);
        };
    }, []);
}

export default useEscape
