import React, {useState} from "react";
import Header from "./Header";
import IDE from "./IDE";
import Tabs from"./Tabs/Tabs";
import Serial from "./Serial/Serial"


function App() {

    const ENTER = '\r\n'; // NEWLINE character

    const [editors, setEditors] = useState([{
        name: "Main",
        id: 0,
        code: ""
    }]);

    const [activeIDE, setActiveIDE] = useState(0);
    const [consoleOutput, setConsoleOutput] = useState([]);


    // Make sure editor names are unique!
    function addREPL(newEditorName) {
        setEditors((prev) => {
            return ([...prev, {
                name: newEditorName,
                id: (prev.length + 1),
                code: ""
            }])
        })
    }

    function pipeOutputToConsole(value) {
        setConsoleOutput((prev) => {
            
            let lines = value.split(">>>");
            lines.forEach(element => {
                element = ">>> " + element;
            });
            if (prev.length === 0 || lines.length === 0)
                return ([...prev, lines]);
                

            return ([...prev, ...lines]);
        })
    }

    return (
        <div>
            <Header />
            <div className="grid grid-cols-2">
                <div className="flex mx-2 justify-center">
                    <Tabs />
                </div>
                <div>
                    <Serial exportConsole={pipeOutputToConsole} />
                </div>
            </div>
            { editors.map((editor) => {
                    return (
                        <IDE key={editor.id} id={editor.id} content={consoleOutput} />
                    )
                })
            }

            

        </div>
    );
}


export default App;
