import React, {useState} from "react";
import Fab from '@mui/material/Fab';
import CableIcon from '@mui/icons-material/Cable';
import Tooltip from "@mui/material/Tooltip";
import PlayCircleIcon from '@mui/icons-material/PlayCircle';


function SerialButton(props) {
    
    const offColor = props.offColor;
    const onColor = props.onColor;

    const [writer, setWriter] = useState(props.writer);

    return (
        <div className="flex">
            <p className="mt-2 mx-2">{props.connectText}</p>

            {/* Displayed when SPIKE is NOT connected */}
            <div className={props.on ? "hidden" : null}>
                <Tooltip title="Connect Device" placement="right">
                    <Fab onClick={() => {props.onClick()}} color={offColor} aria-label="add" size="small">
                        <CableIcon />
                    </Fab>
                </Tooltip>
            </div>

            {/* RESTART Displayed when SPIKE IS connected */}
            <div className={!props.on ? "hidden" : null}>
                <Tooltip title="Restart or Disconnect Device" placement="right">
                    <Fab onClick={() => {props.restartClick()}} color={onColor} aria-label="add" size="small">
                        <CableIcon />
                    </Fab>
                </Tooltip>
            </div>

            {/* RUN BUTTON Displayed when SPIKE IS connected */}
            <p className={!props.on ? "hidden" : "mt-2 mx-2 ml-8"}>Run Code:</p>
            <div className={!props.on ? "hidden" : null}>
                <Tooltip title="Run Current File" placement="right">
                    <Fab 
                        onClick={() => props.runCurrentCode(props.getCurrentCode)} 
                        color="info" 
                        aria-label="add" 
                        size="small">

                        <PlayCircleIcon />
                        
                    </Fab>
                </Tooltip>
            </div>
            
        </div>
        
    )

}

export default SerialButton;
