import React, {useState} from "react";
import Header from "./Header";
import AddREPL from "./AddREPL";
import IDE from "./IDE/IDE";


function App() {

    const [editors, setEditors] = useState([]);


    // Make sure editor names are unique!
    function addEditor(editorName) {
        setEditors((prev) => {
            return ([...prev, editorName])
        })
    }

    return (
        <div>
            <Header />
            <AddREPL />
            {
                editors.map((editor) => {
                    return (
                        <IDE key={editor} />
                    )
                })
            }

            <IDE />
        </div>
    );
}


export default App;
